msg.value = msg.active_power;
msg.time = new Date().toLocaleString("es-AR", { timeZone: "America/Argentina/Buenos_Aires" });

// Alarma desactivada ==> no controla nada
if (global.get('overconsumption_alarm') == false) {
    return;
}

if (msg.value < global.get('overconsumption_ths')) {

    // Valor menor al threshold por primera
    if (flow.get('ap_alarm_down_flag') == false) {
        flow.set('ap_alarm_down_flag', true);
        flow.set('ap_alarm_down_timer', Date.now());
        return;
    }

    // Valor menor al threshold por al menos 1 min ==> se desactiva la alarma
    if (flow.get('ap_alarm_flag') == true && Date.now() - flow.get('ap_alarm_down_timer') > 60000) {
        flow.set('ap_alarm_flag', false);
        return;
    }

    return;
}

// Valor superior al threshold por primera vez
if (flow.get('ap_alarm_flag') == false) {
    flow.set('ap_alarm_flag', true);
    flow.set('ap_alarm_down_flag', false);
    flow.set('ap_alarm_timer', Date.now());
    return;
}

// Valor superior al threshold por más del tiempo permitido ==> se activa la alarma
if (flow.get('ap_alarm_flag') == true && Date.now() - flow.get('ap_alarm_timer') > flow.get('active_power_time_ths')){
    flow.set('ap_alarm_flag', false);
    // Notificaction
    msg.topic = "EXCESO DE CONSUMO!";
    msg.payload = "Se detectó un consumo de " + msg.value + "W";
    // Info
    msg.alarm = "overconsumption";
    msg.threshold = global.get('overconsumption_ths');
    msg.unit = "W";
    return msg;
}

