    let d = new Date(startDate);
    d.setDate(d.getDate() + i);
    let dateStr = d.toISOString().split('T')[0]; // YYYY-MM-DD
    dailyData[dateStr] = 0;
}

// Step 2: Fill in known values from msg.payload
msg.payload.forEach(item => {
    if (dailyData.hasOwnProperty(item.label)) {
        dailyData[item.label] = item.value;
    }
});

// Step 3: Create output messages
let messages = [];
for (let date in dailyData) {
    messages.push({
        topic: date,
        payload: dailyData[date]
    });
}

return [[clearMsg, ...messages]];

