let lastEnergy = null;
let lastTime = null;
let consumption = {};

function formatDateLocal(date) {
    let y = date.getFullYear();
    let m = (date.getMonth() + 1).toString().padStart(2, '0');
    let d = date.getDate().toString().padStart(2, '0');
    return `${y}-${m}-${d}`;
}

function getKey(date) {
    const d = new Date(date);
    switch (timeFrame) {
        case "day":
            return d.getHours(); // 0-23
        case "week":
            return formatDateLocal(d);
    }
}

for (const row of results) {
    const currentEnergy = row.active_energy;
    const currentTime = row.datetime;

    if (lastEnergy !== null && currentEnergy >= lastEnergy) {
        const delta = currentEnergy - lastEnergy;
        const key = getKey(currentTime);
        consumption[key] = (consumption[key] || 0) + delta;
    }

    lastEnergy = currentEnergy;
    lastTime = currentTime;
}

// Convert to array for chart
msg.payload = Object.entries(consumption).map(([label, value]) => ({
    label,
    value
}));

return msg;

